    "coptic_culture_title": "Holy Family Trail - Exploring Coptic Culture",
    "coptic_culture_hero_title": "Exploring Coptic Culture",
    "coptic_culture_hero_subtitle": "A journey through Coptic arts, architecture, and ancient traditions in Egypt",
    "coptic_culture_section_title": "Coptic Culture: An Ancient Heritage",
    "coptic_culture_about_p1": "Coptic culture represents a unique blend of ancient Egyptian heritage, Hellenistic influences, and Christianity, and has maintained its distinctive identity through the centuries.",
    "coptic_culture_about_p2": "Coptic culture is manifested in the plant and geometric decorations that adorn churches, in the icons that blend symbolism and realism, and in the music and hymns whose roots go back to the Pharaonic era.",
    "coptic_culture_aspects_title": "Aspects of Coptic Culture",
    "coptic_culture_art_tab": "Coptic Art",
    "coptic_culture_architecture_tab": "Coptic Architecture",
    "coptic_culture_music_tab": "Music and Hymns",
    "coptic_culture_language_tab": "Coptic Language",
    "coptic_culture_art_title": "Coptic Art and Icons",
    "coptic_culture_art_p1": "Coptic art is characterized by its use of bright colors and clear lines, with a focus on spiritual expression rather than material realism.",
    "coptic_culture_art_p2": "Coptic icons are famous for their rich symbolism and unique techniques that have been passed down through generations.",
    "coptic_culture_architecture_title": "Coptic Architecture",
    "coptic_culture_architecture_p1": "Coptic architecture combines Pharaonic, Roman, and Byzantine elements, with the development of distinctive features such as domes, vaults, and arches.",
    "coptic_culture_architecture_p2": "Coptic churches are distinguished by their use of marble columns and circular arches.",
    "coptic_culture_music_title": "Coptic Music and Hymns",
    "coptic_culture_music_p1": "Coptic music preserves musical characteristics that may date back to the Pharaonic era, especially in the use of rhythms and tones.",
    "coptic_culture_music_p2": "Coptic hymns rely on the system of eight tones developed by Saint Mark.",
    "coptic_culture_language_title": "Coptic Language",
    "coptic_culture_language_p1": "The Coptic language is the final stage of the development of the ancient Egyptian language, written in Greek letters with the addition of some Demotic letters.",
    "coptic_culture_language_p2": "It is still used in prayers and church rituals to this day.",
    "coptic_culture_places_title": "Places to Discover Coptic Culture",
    "coptic_culture_museum_title": "Coptic Museum",
    "coptic_culture_museum_desc": "It houses the largest collection of Coptic antiquities in the world, including manuscripts, icons, textiles, and woodworks.",
    "coptic_culture_museum_link": "Visit the Museum →",
    "coptic_culture_hanging_title": "The Hanging Church",
    "coptic_culture_hanging_desc": "One of the oldest Coptic churches, famous for its unique icons and carved wooden pulpit.",
    "coptic_culture_hanging_link": "Visit the Church →",
    "coptic_culture_wadi_natrun_title": "Wadi El Natrun Monasteries",
    "coptic_culture_wadi_natrun_desc": "A center of Coptic monasticism, preserving its artistic and architectural traditions for centuries.",
    "coptic_culture_wadi_natrun_link": "Visit the Monasteries →",
    "coptic_culture_muharraq_title": "Al-Muharraq Monastery",
    "coptic_culture_muharraq_desc": "It houses an archaeological church dating back to the fourth century AD, with distinctive Coptic arts.",
    "coptic_culture_muharraq_link": "Visit the Monastery →",
    "coptic_culture_events_title": "Coptic Cultural Events",
    "coptic_culture_festival_title": "Annual Coptic Heritage Festival",
    "coptic_culture_festival_place": "Location: Coptic Museum, Cairo",
    "coptic_culture_festival_date": "Date: January 15 to 30 annually",
    "coptic_culture_festival_desc": "The festival includes exhibitions of Coptic icons, concerts of Coptic music and hymns, workshops for traditional crafts, and lectures on Coptic history.",
    "coptic_culture_icons_title": "International Coptic Icons Exhibition",
    "coptic_culture_icons_place": "Location: Cairo Opera House, Cairo",
