document.addEventListener('DOMContentLoaded', () => {
  // 1) RTL/LTR helper
  const RTL_LOCALES = ['ar', 'he', 'fa', 'ur'];
  const isRTL = (lang) => RTL_LOCALES.includes(lang);

  // 2) Mobile-menu toggle
  const mobileToggle = document.getElementById('mobileToggle');
  const navMenu      = document.getElementById('navMenu');
  if (mobileToggle && navMenu) {
    mobileToggle.addEventListener('click', () => {
      const expanded = mobileToggle.getAttribute('aria-expanded') === 'true';
      mobileToggle.setAttribute('aria-expanded', !expanded);
      navMenu.classList.toggle('active');
    });
  }

  // 3) Dropdown menus
  document.querySelectorAll('.nav-item.dropdown > .nav-link').forEach(link => {
    link.addEventListener('click', e => {
      e.preventDefault();
      const menu = link.nextElementSibling;
      if (menu && menu.classList.contains('dropdown-menu')) {
        menu.classList.toggle('show');
      }
    });
  });

  // 4) Close dropdowns on outside click
  document.addEventListener('click', e => {
    if (!e.target.closest('.nav-item.dropdown')) {
      document.querySelectorAll('.dropdown-menu.show').forEach(m => m.classList.remove('show'));
    }
  });

  // 5) Translation engine
  const applyTranslations = (lang) => {
    document.querySelectorAll('[data-i18n]').forEach(el => {
      const key = el.getAttribute('data-i18n');
      if (translations[lang]?.[key]) {
        switch (true) {
          case el.tagName === 'META' && el.getAttribute('name') === 'description':
            el.setAttribute('content', translations[lang][key]);
            break;
          case el.tagName === 'TITLE':
            document.title = translations[lang][key];
            break;
          case el.tagName === 'IMG' && el.getAttribute('alt') === 'مسار العائلة المقدسة':
            el.setAttribute('alt', translations[lang][key]);
            break;
          default:
            el.innerHTML = translations[lang][key];
        }
      }
    });

    // direction & body classes
    const dir = isRTL(lang) ? 'rtl' : 'ltr';
    document.documentElement.setAttribute('dir', dir);
    document.body.classList.toggle('rtl-layout', isRTL(lang));

    // highlight active destination
    const currentPage = window.location.pathname.split('/').pop();
    document.querySelectorAll('.nav-menu .dropdown-menu a').forEach(a => {
      a.classList.toggle('active', a.getAttribute('href') === currentPage);
    });
  };

  // 6) Language switcher
  const langSwitcher = document.getElementById('language-switcher');
  let currentLang = localStorage.getItem('lang') || 'ar';
  if (langSwitcher) langSwitcher.value = currentLang;
  applyTranslations(currentLang);

  // expose global re-translation utility
  window.translatePage = () => applyTranslations(currentLang);

  if (langSwitcher) {
    langSwitcher.addEventListener('change', e => {
      currentLang = e.target.value;
      localStorage.setItem('lang', currentLang);
      applyTranslations(currentLang);
    });
  }

  // 7) Highlight “plan_trip.html” when active
  if (window.location.pathname.includes('plan_trip.html')) {
    document.querySelector('.nav-item .nav-link[href="plan_trip.html"]')?.classList.add('active');
  }
});



